{{#partial "head"}}
<!-- Global site tag (gtag.js) - Google Analytics -->
<script async src="https://www.googletagmanager.com/gtag/js?id=UA-41081365-2"></script>
<script>
  window.dataLayer = window.dataLayer || [];
  function gtag(){dataLayer.push(arguments);}
  gtag('js', new Date());

  gtag('config', 'UA-41081365-2');
</script>

{{{ checkout.checkout_head }}}
{{{ stylesheet '/assets/css/optimized-checkout.css' }}}
{{ getFontsCollection }}
<link rel="stylesheet" href="/content/css/sweetalert2.min.css">
<link rel="stylesheet" href="/content/css/checkout.min.css">
<script type="text/javascript">
    window.language = {{{langJson 'optimized_checkout'}}};
</script>

{{/partial}}

{{#partial "page"}}
<header class="checkoutHeader optimizedCheckout-header">
    <div class="checkoutHeader-content">
        <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
        <h2 class="checkoutHeader-heading">
            <a class="checkoutHeader-link" href="{{urls.home}}">
                {{#if checkout.header_image}}
                    <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                {{ else }}
                    <span class="header-logo-text">{{settings.store_logo.title}}</span>
                {{/if}}
            </a>
        </h2>
    </div>
</header>

{{{ checkout.checkout_content }}}
<div>
    <input type="hidden" name="customer-email" value="{{customer.email}}">
    <input type="hidden" name="customer-group" value="{{customer.customer_group_name}}">
    <input type="hidden" name="store-token" value="{{settings.storefront_api.token}}">
</div>

{{{ footer.scripts }}}


<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.0/jquery.min.js" defer></script>
<script src="/content/js/xhook.min.js" defer></script>
{{#or (if customer.email '==' 'Tachdjian@outlook.com') (if customer.email '==' 'craigssimon@gmail.com')}} 
<script>
    window.addEventListener("DOMContentLoaded",function(){setInterval(()=>{$(".checkout-step--shipping").find("#checkout-shipping-options").length>0&&$(".checkout-step--shipping").find("#checkout-shipping-options").find("li.form-checklist-item").filter(function(){return $(this).find(".shippingOption-desc").eq(0).html().includes("Regular")}).css("display","none")},500)});
</script>
{{/or}}
<script>
    window.__webpack_public_path__ = "{{cdn 'assets/dist/'}}";
</script>
<script src="{{cdn 'assets/dist/theme-bundle.checkout.js'}}" defer></script>

{{/partial}}

{{> layout/empty}}
